import React, { useState, useEffect } from 'react';
import AITextPlatform from './components/AITextPlatform.js';
import Login from './components/Login.js';
import axios from 'axios';
import './App.css';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Check for existing token on app start
    const token = localStorage.getItem('praivio_token');
    const userData = localStorage.getItem('praivio_user');

    if (token && userData) {
      try {
        // Set default authorization header
        axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;

        // Verify token is still valid by making a test request
        axios.get(`${process.env.REACT_APP_API_URL}/`)
          .then(() => {
            setUser(JSON.parse(userData));
            setIsAuthenticated(true);
          })
          .catch(() => {
            // Token is invalid, clear storage
            localStorage.removeItem('praivio_token');
            localStorage.removeItem('praivio_user');
            delete axios.defaults.headers.common['Authorization'];
          })
          .finally(() => {
            setIsLoading(false);
          });
      } catch (error) {
        console.error('Error parsing user data:', error);
        localStorage.removeItem('praivio_token');
        localStorage.removeItem('praivio_user');
        setIsLoading(false);
      }
    } else {
      setIsLoading(false);
    }
  }, []);

  const handleLoginSuccess = (userData) => {
    setUser(userData);
    setIsAuthenticated(true);
  };

  const handleLogout = () => {
    // Clear local storage
    localStorage.removeItem('praivio_token');
    localStorage.removeItem('praivio_user');

    // Clear axios headers
    delete axios.defaults.headers.common['Authorization'];

    // Update state
    setUser(null);
    setIsAuthenticated(false);
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 flex items-center justify-center">
        <div className="text-center">
          <div className="w-16 h-16 border-4 border-purple-500 border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-white text-lg">Lade Praivio...</p>
        </div>
      </div>
    );
  }

  if (!isAuthenticated) {
    return <Login onLoginSuccess={handleLoginSuccess} />;
  }

  return <AITextPlatform user={user} onLogout={handleLogout} />;
}

export default App;
